const discord = require("./src/index")
const textEmbed = require("./textEmbed.js")
const client = new discord.Client()

client.login(process.argv[2])
client.on("ready", () => {
    
})
client.on("message", (message) => {
    if(message.content === "/test" && message.author.id === client.user.id){
        let embed = new textEmbed()
        .setImage("https://cdn.discordapp.com/attachments/547819330344321034/552833417113370625/image0-2-1.jpg")
        .setFooter(client.user.username, client.user.displayAvatarURL)
        message.edit(embed)
    }
})